/**
* Primary factory class
*/
FavoritesAdmin.Factory = function()
{
	var plugin = this;
	var $ = jQuery;

	plugin.build = function()
	{
		new FavoritesAdmin.Settings;
		new FavoritesAdmin.ListingCustomizer;
	}

	return plugin.build();
}